// R√©cup√®re la r√©ponse de l'agent
let output = $input.first().json.output;

// Remplace le markdown standard par la syntaxe WhatsApp/Telegram
output = output
  .replace(/\*\*\*(.+?)\*\*\*/g, '*_$1_*')  // ***gras+italique*** ‚Üí *_texte_*
  .replace(/\*\*(.+?)\*\*/g, '*$1*')        // **gras** ‚Üí *gras*
  .replace(/__(.+?)__/g, '_$1_')            // __italique__ ‚Üí _italique_
  .replace(/~~(.+?)~~/g, '~$1~');           // ~~barr√©~~ ‚Üí ~barr√©~

return [{
  json: {
    output: output,
    chat_id: $('Telegram Trigger').first().json.message.chat.id
  }
}];
